import { Component } from "react";
import axios from "axios";
import Cookies from "js-cookie";
import Header from "../Header";

class Game extends Component {
  state = { userInput: "", zigzagWord: {}, check: false };

  onClickUserInput = (event) => {
    this.setState({ userInput: event.target.value });
  };

  componentDidMount = () => {
    this.getZigzagWord();
  };

  getZigzagWord = () => {
    const jwtToken = Cookies.get("jwt_token");
    const api = axios.create({
      baseURL: "http://localhost:4000",
    });
    api.defaults.headers.common["Authorization"] = `Bearer ${jwtToken}`;
    api
      .get("/game")
      .then((response) => {
        this.setState({ zigzagWord: response.data });
      })
      .catch((error) => {
        console.error(error);
      });
  };

  onClickNext = () => {
    this.getZigzagWord();
    this.setState({ check: false });
  };

  onClickCheck = () => {
    this.setState({ check: true });
  };

  render() {
    const { userInput, zigzagWord, check } = this.state;
    let zigzagWordLength = 0;
    if (zigzagWord.zigzagWord !== undefined) {
      zigzagWordLength = zigzagWord.zigzagWord.length;
    }
    const checked = userInput === zigzagWord.correctWord ? "correct " : "wrong";
    return (
      <>
        <Header />
        <div>
          <p>{zigzagWord.zigzagWord}</p>
          {userInput.length < zigzagWordLength ? (
            <input
              type="text"
              value={userInput}
              onChange={this.onClickUserInput}
            />
          ) : (
            <span>{userInput}</span>
          )}
          <button type="button" onClick={this.onClickCheck}>
            Check
          </button>
          <button type="button" onClick={this.onClickNext}>
            Next
          </button>
          {check && <p>{checked}</p>}
        </div>
      </>
    );
  }
}
export default Game;
