import { useState, useEffect } from "react";
import axios from "axios";
import Cookies from "js-cookie";
import Header from "../Header";

const Game = () => {
  const [userInput, setUserInput] = useState("");
  const [zigzagWord, setZigzagWord] = useState({});

  const jwtToken = Cookies.get("jwt_token");

  useEffect(() => {
    const getZigzagWord = () => {
      const api = axios.create({
        baseURL: "http://localhost:4000",
      });
      api.defaults.headers.common["Authorization"] = `Bearer ${jwtToken}`;
      api
        .get("/game")
        .then((response) => {
          setZigzagWord(response.data);
        })
        .catch((error) => {
          console.error(error);
        });
    };
    getZigzagWord();
  }, [jwtToken]);

  const onClickNext = () => {
    const api = axios.create({
      baseURL: "http://localhost:4000",
    });
    api.defaults.headers.common["Authorization"] = `Bearer ${jwtToken}`;
    api
      .get("/game")
      .then((response) => {
        setZigzagWord(response.data);
      })
      .catch((error) => {
        console.error(error);
      });
  };

  return (
    <>
      <Header />
      <div>
        <p>{zigzagWord.zigzagWord}</p>
        <input
          type="text"
          value={userInput}
          onChange={(event) => {
            setUserInput(event.target.value);
          }}
        />
        <button type="button" onClick={onClickNext}>
          Next
        </button>
      </div>
    </>
  );
};
export default Game;
