import { Component } from "react";
import axios from "axios";
import Cookies from "js-cookie";
import Header from "../Header";

class Game extends Component {
  state = { userInput: "", zigzagWord: {}, check: "", score: 0 };

  onClickUserInput = (event) => {
    this.setState({ userInput: event.target.value });
  };

  componentDidMount = () => {
    this.getZigzagWord();
  };

  getZigzagWord = () => {
    const { score } = this.state;
    console.log(score);
    const jwtToken = Cookies.get("jwt_token");
    const api = axios.create({
      baseURL: "http://localhost:4000",
    });
    api.defaults.headers.common["Authorization"] = `Bearer ${jwtToken}`;
    api
      .get("/game")
      .then((response) => {
        this.setState({ zigzagWord: response.data });
      })
      .catch((error) => {
        console.error(error);
      });
  };

  onClickNext = () => {
    this.getZigzagWord();
    this.setState({ check: "", userInput: "" });
  };

  onClickCheck = () => {
    const { userInput, zigzagWord } = this.state;
    const check = userInput === zigzagWord.correctWord ? "correct" : "wrong";
    if (userInput === zigzagWord.correctWord) {
      this.setState((prevState) => ({
        score: prevState.score + 1,
      }));
    }
    this.setState({ check });
  };

  render() {
    const { userInput, zigzagWord, check } = this.state;
    return (
      <>
        <Header />
        <div>
          <p>{zigzagWord.zigzagWord}</p>
          <input
            type="text"
            value={userInput}
            onChange={this.onClickUserInput}
          />
          <button type="button" onClick={this.onClickCheck}>
            Check
          </button>
          <button type="button" onClick={this.onClickNext}>
            Next
          </button>
          <p>{check}</p>
        </div>
      </>
    );
  }
}
export default Game;
